// SPDX-License-Identifier: GPL-3.0

pragma solidity >0.4.99;

contract Betting {
    address payable public owner;
    uint256 public minimumBet;
    uint256 public numberOfBets;
    uint256 public maxAmountOfBets = 1000;

    struct Bet {
        uint256 matchId;
        uint256 totalBetHome;
        uint256 totalBetAway;
        uint256 totalBetDraw;
        address payable[] players;
    }

    address payable[] addr;

    Bet[] betsList;

    struct playerBet {
        uint256 matchId;
        uint256 amountBet;
        string teamSelected;
    }

    mapping(address => playerBet[]) public playerBetsList;

    constructor() public {
        owner = msg.sender;
        minimumBet = 100000000000000;
    }

    function kill() public {
        if (msg.sender == owner) selfdestruct(owner);
    }

    function checkPlayerExists(address player, uint256 matchId)
        public
        view
        returns (bool)
    {
        for (uint256 i = 0; i < betsList.length; i++) {
            if (betsList[i].matchId == matchId) {
                for (uint256 j = 0; j < betsList[i].players.length; j++) {
                    if (betsList[i].players[j] == player) return true;
                }
            }
        }
        return false;
    }

    function checkBetExists(uint256 matchId) public view returns (bool) {
        for (uint256 i = 0; i < betsList.length; i++) {
            if (betsList[i].matchId == matchId) return true;
        }
        return false;
    }

    function setBet(uint256 _matchId, string memory _teamSelected)
        public
        payable
    {
        require(!checkPlayerExists(msg.sender, _matchId));
        require(msg.value >= minimumBet);

        playerBetsList[msg.sender].push(
            playerBet(_matchId, msg.value, _teamSelected)
        );

        if (!checkBetExists(_matchId)) {
            addr.push(msg.sender);
            if (
                keccak256(abi.encodePacked(_teamSelected)) ==
                keccak256(abi.encodePacked("home"))
            ) {
                betsList.push(Bet(_matchId, msg.value, 0, 0, addr));
            } else if (
                keccak256(abi.encodePacked(_teamSelected)) ==
                keccak256(abi.encodePacked("away"))
            ) {
                betsList.push(Bet(_matchId, 0, msg.value, 0, addr));
            } else {
                betsList.push(Bet(_matchId, 0, 0, msg.value, addr));
            }
            delete addr;
        } else {
            for (uint256 i = 0; i < betsList.length; i++) {
                if (betsList[i].matchId == _matchId) {
                    betsList[i].players.push(msg.sender);
                    if (
                        keccak256(abi.encodePacked(_teamSelected)) ==
                        keccak256(abi.encodePacked("home"))
                    ) {
                        betsList[i].totalBetHome += msg.value;
                    } else if (
                        keccak256(abi.encodePacked(_teamSelected)) ==
                        keccak256(abi.encodePacked("away"))
                    ) {
                        betsList[i].totalBetAway += msg.value;
                    } else {
                        betsList[i].totalBetDraw += msg.value;
                    }
                }
            }
        }
    }

    function distributePrizes(uint256 matchId, string memory teamWinner)
        public
    {
        address payable[1000] memory winners;
        uint256 count = 0; // This is the count for the array of winners
        uint256 LoserBet = 0; //This will take the value of all losers bet
        uint256 WinnerBet = 0; //This will take the value of all winners bet
        address add;
        uint256 bet;
        uint256 betIndex;
        address payable playerAddress;

        for (uint256 i = 0; i < betsList.length; i++) {
            if (betsList[i].matchId == matchId) {
                for (uint256 j = 0; j < betsList[i].players.length; j++) {
                    playerAddress = betsList[i].players[j];
                    for (
                        uint256 k = 0;
                        k < playerBetsList[playerAddress].length;
                        k++
                    ) {
                        if (
                            matchId == playerBetsList[playerAddress][k].matchId
                        ) {
                            betIndex = k;
                            if (
                                keccak256(
                                    abi.encodePacked(
                                        playerBetsList[playerAddress][k]
                                            .teamSelected
                                    )
                                ) == keccak256(abi.encodePacked(teamWinner))
                            ) {
                                winners[count] = playerAddress;
                                count++;
                            }
                        }
                    }
                }
                if (
                    keccak256(abi.encodePacked(teamWinner)) ==
                    keccak256(abi.encodePacked("home"))
                ) {
                    LoserBet =
                        betsList[i].totalBetAway +
                        betsList[i].totalBetDraw;
                    WinnerBet = betsList[i].totalBetHome;
                } else if (
                    keccak256(abi.encodePacked(teamWinner)) ==
                    keccak256(abi.encodePacked("away"))
                ) {
                    LoserBet =
                        betsList[i].totalBetHome +
                        betsList[i].totalBetDraw;
                    WinnerBet = betsList[i].totalBetAway;
                } else {
                    LoserBet =
                        betsList[i].totalBetHome +
                        betsList[i].totalBetAway;
                    WinnerBet = betsList[i].totalBetDraw;
                }
            }
        }

        for (uint256 i = 0; i < count; i++) {
            if (winners[i] != address(0)) add = winners[i];
            bet = playerBetsList[add][betIndex].amountBet;
            winners[i].transfer(
                (bet * (10000 + ((LoserBet * 10000) / WinnerBet))) / 10000
            );
        }

        LoserBet = 0;
        WinnerBet = 0;
    }

    function AmountHome(uint256 matchId) public view returns (uint256) {
        if (!checkBetExists(matchId)) {
            return 0;
        } else {
            for (uint256 i = 0; i < betsList.length; i++) {
                if (betsList[i].matchId == matchId)
                    return betsList[i].totalBetHome;
            }
        }
    }

    function AmountAway(uint256 matchId) public view returns (uint256) {
        if (!checkBetExists(matchId)) {
            return 0;
        } else {
            for (uint256 i = 0; i < betsList.length; i++) {
                if (betsList[i].matchId == matchId)
                    return betsList[i].totalBetAway;
            }
        }
    }

    function AmountDraw(uint256 matchId) public view returns (uint256) {
        if (!checkBetExists(matchId)) {
            return 0;
        } else {
            for (uint256 i = 0; i < betsList.length; i++) {
                if (betsList[i].matchId == matchId)
                    return betsList[i].totalBetDraw;
            }
        }
    }
}
